mapper.select:
select a.* , t2.ProjectName as ProjectIDLinkText, t5.TeamName as TeamIDLinkText, t6.real_name as CreateUserIDLinkText, t7.dict_label as StatusLinkText from ach_appraisal a left join Aud_Project t2 on a.ProjectID = t2.ProjectID left join PM_Team t5 on a.TeamID = t5.TeamID join sys_user t6 on a.CreateUserID = t6.user_id left join (select * from sys_dict_data where dict_type='科技成果状态') t7 on a.Status = t7.dict_value where 0 = 0
and a.Status = #{status,jdbcType=INTEGER}
and a.publishyear = #{publishyear,jdbcType=INTEGER}
and a.teamid = #{teamid, jdbcType=INTEGER}
order by a.appraisalid desc

mapper.insert:
insert into ACH_Appraisal ( AppraisalName, BookCode,
  CertificateUnit, PublishYear, Opinion, 
  Memo, ProjectID, TeamID, 
  CreateUserID, CreateTime, Status
  )
values ( #{appraisalname,jdbcType=VARCHAR}, #{bookcode,jdbcType=VARCHAR},
  #{certificateunit,jdbcType=VARCHAR}, #{publishyear,jdbcType=INTEGER}, #{opinion,jdbcType=VARCHAR}, 
  #{memo,jdbcType=VARCHAR}, #{projectid,jdbcType=INTEGER}, #{teamid,jdbcType=INTEGER}, 
  #{createuserid,jdbcType=INTEGER}, #{createtime,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}
  )

mapper.update:
update ACH_Appraisal
set
    Status = #{status,jdbcType=INTEGER}
where AppraisalID = #{appraisalid,jdbcType=INTEGER}
