mapper.select:
select a.* , t1.dict_label as ArticleTypeLinkText, t2.ProjectName as ProjectIDLinkText, t5.TeamName as TeamIDLinkText, t6.real_name as CreateUserIDLinkText, t7.dict_label as StatusLinkText from ach_Article a left join (select * from sys_dict_data where dict_type='著作类别') t1 on a.ArticleType = t1.dict_value left join Aud_Project t2 on a.ProjectID = t2.ProjectID left join PM_Team t5 on a.TeamID = t5.TeamID join sys_user t6 on a.CreateUserID = t6.user_id left join (select * from sys_dict_data where dict_type='科技成果状态') t7 on a.Status = t7.dict_value where 0=0 and a.status != 39
and a.Status = #{status,jdbcType=INTEGER}
and a.publishyear = #{publishyear,jdbcType=INTEGER}
and a.articletype = #{articletype,jdbcType=INTEGER}

mapper.insert:

insert into ACH_Article (ArticleID, ArticleName, ArticleType, 
  PublishYear, PublishUnit, PublishNumber, 
  Memo, ProjectID, TeamID, 
  CreateUserID, CreateTime, Status
  )
values (#{articleid,jdbcType=INTEGER}, #{articlename,jdbcType=VARCHAR}, #{articletype,jdbcType=INTEGER}, 
  #{publishyear,jdbcType=INTEGER}, #{publishunit,jdbcType=VARCHAR}, #{publishnumber,jdbcType=VARCHAR}, 
  #{memo,jdbcType=VARCHAR}, #{projectid,jdbcType=INTEGER}, #{teamid,jdbcType=INTEGER}, 
  #{createuserid,jdbcType=INTEGER}, #{createtime,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}
  )
