mapper.select:
select a.* , t1.dict_label as PublishBookLevelLinkText, t5.TeamName as TeamIDLinkText, t6.real_name as CreateUserIDLinkText, t7.dict_label as StatusLinkText from ACH_Thesis a left join (select * from sys_dict_data where dict_type='期刊级别') t1 on a.PublishBookLevel = t1.dict_value left join PM_Team t5 on a.TeamID = t5.TeamID join sys_user t6 on a.CreateUserID = t6.user_id left join (select * from sys_dict_data where dict_type='科技成果状态') t7 on a.Status = t7.dict_value where 0=0 and a.status != 39
and a.Status = #{status,jdbcType=INTEGER}
and a.thesislevel = #{thesislevel,jdbcType=VARCHAR}
and a.year = #{year,jdbcType=INTEGER}
and a.teamid = #{teamid,jdbcType=INTEGER}
order by a.ThesisLevel, a.thesisname

select count(*) from ACH_Thesis
where status != 39 and thesisname = #{thesisname,jdbcType=VARCHAR}

  and thesisid != #{thesisid,jdbcType=INTEGER}
