mapper.select:
select RequestID, TeamID, Year, Status, ConfirmUserID, ConfirmTime
from PER_ConfirmQequest a
where 0=0

  and a.teamid = #{teamid,jdbcType=INTEGER}


  and a.year = #{year,jdbcType=INTEGER}

mapper.insert:
insert into PER_ConfirmQequest ( TeamID, Year,
                                Status, ConfirmUserID, ConfirmTime
)
values ( #{teamid,jdbcType=INTEGER}, #{year,jdbcType=INTEGER},
        #{status,jdbcType=VARCHAR}, #{confirmuserid,jdbcType=INTEGER}, #{confirmtime,jdbcType=VARCHAR}
       )

mapper.update:
update PER_ConfirmQequest
set Status = #{status,jdbcType=VARCHAR},
    ConfirmUserID = #{confirmuserid,jdbcType=INTEGER},
    ConfirmTime = #{confirmtime,jdbcType=VARCHAR}
where TeamID = #{teamid,jdbcType=INTEGER} and Year = #{year,jdbcType=INTEGER}

mapper.select:

