mapper.select:
select a.* ,t1.dict_label as ProjectTypeLinkText from PER_IndicatorProject a left join (select * from sys_dict_data where dict_type='项目类型') t1 on a.ProjectType = t1.dict_value where a.IfDeleted = 0
and t1.dict_label like concat('%', #{projecttypelinktext}, '%')
order by ProjectTypeLinkText;

mapper.insert:
insert into PER_IndicatorProject ( ProjectType, JoinType, Points, IfDeleted )
values (#{projecttype,jdbcType=INTEGER},
        #{jointype,jdbcType=VARCHAR}, #{points,jdbcType=DECIMAL}, #{ifdeleted,jdbcType=BIT}
       )

mapper.update:
update PER_IndicatorProject
set ProjectType = #{projecttype,jdbcType=INTEGER},
  JoinType = #{jointype,jdbcType=VARCHAR},
  Points = #{points,jdbcType=DECIMAL}
where IndicatorProjectID = #{indicatorprojectid,jdbcType=INTEGER}

mapper.select:

select IndicatorProjectID, ProjectType, JoinType, Points, IfDeleted
from PER_IndicatorProject
where IndicatorProjectID = #{indicatorprojectid,jdbcType=INTEGER}
