mapper.select:
select TeamID, TeamName, TeamLeaderID, Memo, CreateUserID, CreateTime, IfDeleted
from PM_Team
where TeamID = #{teamid,jdbcType=INTEGER}

SELECT a.TeamID, a.TeamName, a.TeamLeaderID, a.Memo, a.CreateUserID, a.CreateTime, a.IfDeleted , t2.Real_Name AS TeamLeaderIDLinkText, t4.Real_Name AS CreateUserIDLinkText FROM PM_Team a left join sys_user t4 on a.CreateUserID = t4.user_id left join sys_user t2 on a.TeamLeaderID= t2.user_id where a.IfDeleted = 0
AND a.TeamID = #{teamid, jdbcType=INTEGER}
AND a.TeamName like concat('%', #{teamname}, '%')

mapper.delete:
delete from PM_Team
where TeamID in

  #{teamid}
