mapper.insert:
insert into PM_TeamMember (TeamID, TeamRole, UserID
  )
values (#{teamid,jdbcType=INTEGER}, #{teamrole,jdbcType=INTEGER}, #{userid,jdbcType=INTEGER}
  )

mapper.select:
select a.TeamID, a.TeamRole, a.UserID,b.real_name,c.dict_label as TeamRoleName from PM_TeamMember a, sys_user b, sys_dict_data c where a.UserID=b.user_id and a.TeamRole=c.dict_value and c.dict_type = '团队角色'
AND a.TeamID = #{teamid,jdbcType=INTEGER}
order by a.TeamID,a.TeamRole, b.real_name
