AND b.SheetType = #{sheettype, jdbcType=VARCHAR}
AND a.ProjectID = #{projectid, jdbcType=INTEGER}
and a.SupplierID in
#{item}
union select a.PayID ,a.SheetID ,a.ProjectID ,a.SupplierID ,a.zong ,a.xiaoji ,a.yiqian ,a.bennian ,a.benci ,a.AuditTime ,t1.ProjectName as ProjectIDLinkText ,t2.SupplierName as SupplierIDLinkText ,'' as sheettype from Aud_BudgetPay a join Aud_Project t1 on t1.ProjectID = a.ProjectID join SRM_SupplierInfo t2 on t2.SupplierID = a.SupplierID where a.SheetID = -1 and a.AuditTime is not null
AND a.ProjectID = #{projectid, jdbcType=INTEGER}
and a.SupplierID in
