mapper.select:
select a.photo_id, a.photo_size_value, a.photo_text, a.created, a.modified, a.sku_id, a.status, b.dict_label as photo_size_label , b.dict_sort from sku_photo a left join (select * from sys_dict_data where dict_type = '图片尺寸类别' ) b on a.photo_size_value= b.dict_value where a.status=1
and a.sku_id = #{skuId,jdbcType=BIGINT}
order by b.dict_sort


select photo_id, photo_size_value, created, modified, sku_id, status, photo_text
from sku_photo
where photo_id = #{photoId,jdbcType=BIGINT}
